# ---------------------------- Support functions --------------------------

def not_valid_names(model_name, objects)
  names = []
  objects.each do |obj|
    object = model_name.new(obj)
    object.valid? ? true : names << [ object.name, object.errors.messages ]
  end
  puts names
end

def join_schools_with_classes(school_object_name, school_classes_array)
  school_object_name.all.each do |school|
    school_classes = school_classes_array[school.name]
    if school_classes
      school_classes.each do |class_name|
        school_class = SchoolClass.find_by(name: class_name)
        school.class_of_schools.create( school_class_id: school_class.id)
      end
    else
      puts school.name
    end
  end
end

def join_school_with_skills(school_object_name, skills_list_name)
  school_object_name.all.each do |school|
    school_skills = skills_list_name[school.name]    
    if school_skills
      school_skills.each do |skill|
        skill_object = Skill.find_by(name: skill[:name])
        if skill_object
          school.skill_of_objects.create( skill_id: skill_object.id,
                                          skill_emphasis: skill[:school_emphasis],
                                          value: skill[:value] || 1 )
        else
          p skill
        end
      end
    else
      puts school.name
    end    
  end
end

def join_school_with_ranks(object, rank_array)
  rank_array.each do |school|
    school_object = object.find_by( name: school[:school_name] )
    if school_object
        school[:school_ranks].each do |rank|
          school_object.school_ranks.create(name: rank[:rank_name], desc: rank[:rank_desc],
                                            rank_lvl: rank[:rank_lvl] || 1 )
        end      
    else
      p school[:school_name]
    end
  end
end
