puts 'Generating spells and element, ring, tags relations'
spell_list = Spell.all
spell_list.each do |spell|
  spell_element = SpellElement.find_by( name: spell_element_ring_and_tags[spell.name][:element])
  spell_ring = SpellRing.find_by( name: spell_element_ring_and_tags[spell.name][:ring])
  spell_tags = []
  if spell_element_ring_and_tags[spell.name][:tags].present?
    spell_element_ring_and_tags[spell.name][:tags].each do |tag|
      tag.present? ? spell_tags << SpellTag.find_by( name: tag) : nil
    end
  end
  spell.element_of_spells.create( spell_element_id: spell_element.id )
  spell.update_attribute(:spell_ring_id, spell_ring.id)
  if spell_tags.any?
    spell_tags.each do |tag|
      spell.tag_of_spells.create( spell_tag_id: tag.id)
    end
  end
end
